DROP DATABASE PROYECTO;

CREATE DATABASE PROYECTO;

USE PROYECTO;

-- Tablas

CREATE TABLE IF NOT EXISTS USUARIOS(
	ID_USUARIO INT NOT NULL AUTO_INCREMENT,
	NICK VARCHAR(15) NOT NULL,
	CLAVE VARCHAR(50) NOT NULL,
	ROL VARCHAR(20) NOT NULL,
	CONSTRAINT PK_USUARIOS PRIMARY KEY (ID_USUARIO),
        CONSTRAINT UK_USUARIOS UNIQUE (NICK) 
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS DATOS(
	ID_USUARIO INT NOT NULL AUTO_INCREMENT,
	NOMBRE VARCHAR(50) NOT NULL,
	APELLIDOS VARCHAR(80) NOT NULL,
	TELEFONO VARCHAR(15) NOT NULL,
	EMAIL VARCHAR(50) NOT NULL,
	CIUDAD VARCHAR(50) NOT NULL,
	DIRECCION VARCHAR(80) NOT NULL,
	COD_POSTAL VARCHAR(10) NOT NULL,
	PAIS VARCHAR(50) NOT NULL,
	FECHA_NAC DATE NOT NULL, 
	SEXO VARCHAR(10) NOT NULL,
	N_TARJETA VARCHAR(20),
	CONSTRAINT PK_DATOS PRIMARY KEY (ID_USUARIO),
	CONSTRAINT FK_DATOS_USUARIOS FOREIGN KEY (ID_USUARIO) REFERENCES USUARIOS (ID_USUARIO) ON DELETE CASCADE,
        CONSTRAINT UK_DATOS UNIQUE (EMAIL)
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS ACCESO(
	ID_ACCESO INT NOT NULL AUTO_INCREMENT,
	FECHA DATETIME NOT NULL,
	ID_USUARIO INT,
	NICK VARCHAR(15) NOT NULL,
	CONSTRAINT PK_ACCESO PRIMARY KEY(ID_ACCESO,FECHA),
	CONSTRAINT FK_ACCESO_USUARIOS FOREIGN KEY (ID_USUARIO) REFERENCES USUARIOS (ID_USUARIO) ON DELETE SET NULL
)ENGINE=INNODB;

GRANT ALL PRIVILEGES ON PROYECTO.* TO adminImpact@localhost IDENTIFIED BY 'adminImpact';

CREATE TABLE IF NOT EXISTS PELICULAS(
	ID_PELICULA INT NOT NULL AUTO_INCREMENT,
	TITULO VARCHAR(50) NOT NULL,
	DESCRIPCION VARCHAR(140) NOT NULL,
	CRITICA VARCHAR(2000) NOT NULL,
        ANIO INT(4) NOT NULL,
	DURACION INT(3) NOT NULL,
        DIRECTOR VARCHAR(50) NOT NULL,
        PAIS VARCHAR(30) NOT NULL,
        IMAGEN VARCHAR(30) NOT NULL,
        VIDEO VARCHAR(60) NOT NULL,
        ESTRENO BOOLEAN NOT NULL,
        PRECIO DECIMAL(5,2) NOT NULL,
	CONSTRAINT PK_PELICULAS PRIMARY KEY (ID_PELICULA),
        CONSTRAINT UK_PELICULAS UNIQUE (TITULO)
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS ACTORES(
    ID_ACTOR INT NOT NULL AUTO_INCREMENT,
    NOMBRE_APE VARCHAR(50) NOT NULL,
    NACIONALIDAD VARCHAR(30) NOT NULL,
    FECHA_NAC DATE NOT NULL,
    DESCRIPCION VARCHAR(140) NOT NULL,
    CARRERA VARCHAR(2000) NOT NULL,
    IMAGEN VARCHAR(30) NOT NULL,
    CONSTRAINT PK_ACTORES PRIMARY KEY (ID_ACTOR),
    CONSTRAINT UK_ACTORES UNIQUE (NOMBRE_APE)
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS ACTUA(
    ID_ACTOR INT NOT NULL,
    ID_PELICULA INT NOT NULL,
    CONSTRAINT PK_ACTUA PRIMARY KEY (ID_ACTOR, ID_PELICULA),
    CONSTRAINT FK_ACTUA_PELICULAS FOREIGN KEY (ID_PELICULA) REFERENCES PELICULAS (ID_PELICULA) ON DELETE CASCADE,
    CONSTRAINT FK_ACTUA_ACTORES FOREIGN KEY (ID_ACTOR) REFERENCES ACTORES (ID_ACTOR) ON DELETE CASCADE
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS GENEROS(
    ID_GENERO INT NOT NULL AUTO_INCREMENT,
    NOM_GENERO VARCHAR(20) NOT NULL,
    CONSTRAINT PK_GENEROS PRIMARY KEY (ID_GENERO),
    CONSTRAINT UK_GENEROS UNIQUE (NOM_GENERO)
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS GEN_PEL(
    ID_GENERO INT NOT NULL,
    ID_PELICULA INT NOT NULL,
    CONSTRAINT PK_GEN_PEL PRIMARY KEY (ID_GENERO, ID_PELICULA),
    CONSTRAINT FK_GEN_PEL_PELICULAS FOREIGN KEY (ID_PELICULA) REFERENCES PELICULAS (ID_PELICULA) ON DELETE CASCADE,
    CONSTRAINT FK_GEN_PEL_GENEROS FOREIGN KEY (ID_GENERO) REFERENCES GENEROS (ID_GENERO) ON DELETE CASCADE
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS NOTICIAS(
    ID_NOTICIA INT NOT NULL AUTO_INCREMENT,
    TITULO VARCHAR(50) NOT NULL,
    DESCRIPCION VARCHAR(140) NOT NULL,
    TEXTO_NOTICIA VARCHAR (2000) NOT NULL,
    IMAGEN VARCHAR(30) NOT NULL,
    VIDEO VARCHAR(60),
    FECHA DATE NOT NULL,
    ID_PELICULA INT DEFAULT NULL,
    ID_ACTOR INT DEFAULT NULL,
    CONSTRAINT PK_NOTICIAS PRIMARY KEY (ID_NOTICIA),
    CONSTRAINT FK_NOTICIAS_PELICULAS FOREIGN KEY (ID_PELICULA) REFERENCES PELICULAS (ID_PELICULA) ON DELETE SET NULL,
    CONSTRAINT FK_NOTICIAS_ACTORES FOREIGN KEY (ID_ACTOR) REFERENCES ACTORES (ID_ACTOR) ON DELETE SET NULL,
    CONSTRAINT FK_NOTICIAS UNIQUE (TITULO)
)ENGINE=INNOBD;

CREATE TABLE IF NOT EXISTS VENTAS(
	ID_VENTA INT NOT NULL AUTO_INCREMENT,
    ID_PELICULA INT,
    ID_USUARIO INT,
	PELICULA VARCHAR(50),
	NICK VARCHAR(15),
    FECHA_HORA DATETIME NOT NULL,
    CANTIDAD INT NOT NULL,
    CONSTRAINT PK_VENTAS PRIMARY KEY (ID_VENTA),
    CONSTRAINT FK_VENTAS_PELICULAS FOREIGN KEY (ID_PELICULA) REFERENCES PELICULAS (ID_PELICULA) ON DELETE SET NULL,
    CONSTRAINT FK_VENTAS_USUARIOS FOREIGN KEY (ID_USUARIO) REFERENCES USUARIOS (ID_USUARIO) ON DELETE SET NULL
)ENGINE=INNODB;

CREATE TABLE IF NOT EXISTS COMENTARIOS(
    ID_COMENTARIO INT NOT NULL AUTO_INCREMENT,
    ID_USUARIO INT NOT NULL,
    ID_PELICULA INT NOT NULL,
    FECHA_COMENT DATETIME NOT NULL,
    COMENTARIO VARCHAR(140) NOT NULL,
    CONSTRAINT PK_COMENTARIOS PRIMARY KEY (ID_COMENTARIO),
    CONSTRAINT FK_COMENTARIOS_USUARIOS FOREIGN KEY (ID_USUARIO) REFERENCES USUARIOS (ID_USUARIO) ON DELETE CASCADE,
    CONSTRAINT FK_COMENTARIOS_PELICULAS FOREIGN KEY (ID_PELICULA) REFERENCES PELICULAS (ID_PELICULA) ON DELETE CASCADE,
    CONSTRAINT UK_COMENTARIOS UNIQUE (FECHA_COMENT)
)ENGINE=INNODB;

